import React, { useState } from 'react';
import {
  Form,
  Button,
  Select,
  Space,
} from 'antd';
import SendRequest from './SendRequest';

const DeleteMentor = ({mentors}) => {
  const [componentSize, setComponentSize] = useState('default');

  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  const listMentors = mentors.map((row) => (<Select.Option value={row.mentor}>{row.mentor}</Select.Option>));
  return (
    <>
       <Space><h1>Delete Mentor</h1></Space>
      <Form
        name='Delete Mentor'
        labelCol={{
          span: 7,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="Horizontal"
        initialValues={{
          size: 'small',
        }}
        onValuesChange={onFormLayoutChange}
      >
        <Form.Item label="Select Mentor">
          <Select>
            {listMentors}  
          </Select>
        </Form.Item>
        <Form.Item style={{paddingLeft: 143}}>
          <SendRequest />
        </Form.Item>
      </Form>
    </>
  );
};


export default DeleteMentor;
