    let result = getData();
    console.log(result);
    companies = result;
  }, []);

  const list_comp = companies.map((obj) => {
  return obj.company;
});
const list_comp_colors = list_comp.map((comp, index) => {
  let col = colors[index];
  return { company: comp, color: col };
});
  const AM = [
    {
      title: "Mentor",
