import React from "react";
import { Button } from "antd";

const RescheduleButton = ({ view, record, setPossibleMeetings, setView }) => {


  const cancelMeeting = () => {
      console.log(JSON.stringify([record]));
    if (view == 0) {
    fetch(
          "http://localhost:3033/api/reschedule" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify([record]),
          }
        )
          .then((response) => response.json())
          .then((result) => {
            setPossibleMeetings(result);
            setView(1);
          })
          .catch((error) => {
            console.log("Error:", error);
          });
    } else {
      fetch(
          "http://localhost:3033/api/meetings" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify([record]),
          }
        )
          .then((response) => response.json())
          .then((result) => {
            alert(result.message);
            setView(0);
          })
          .catch((error) => {
            console.log("Error:", error);
          });

    }

  };
  buttonMessage = {Reschedule, Schedule};
  return (
    <div>
      <Button type="link" onClick={cancelMeeting}>
        {buttonMessage[view]}
      </Button>
    </div>
  );
};

export default RescheduleButton;
