import React, { useState } from 'react';
import {
  Form,
  Input,
  Button,
  Select,
  Space
} from 'antd';
const { Option } = Select;

const AddMentor = () => {

  const cancelMentor = (value) => {
    let bodyData = JSON.stringify({"mentor": value.mentor});
    console.log(bodyData);
    fetch(
          "http://localhost:3033/api/mentors" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "Delete",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: bodyData,
          }
        )
          .then((response) => response.json())
          .then((result) => {
              alert('Mentor Deleted');
              setReloadMentors(true);
          })
          .catch((error) => {
            alert("Error:", error);
          });
  };

  return (
    <>
      <Space><h1>Add Mentor</h1></Space>
      <Form
        labelCol={{
          span: 8,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="horizontal"
        initialValues={{
          size: 'small',
        }}
      >
        <Form.Item label="Mentor Name" rules={[
          {
            required: true,
            message: 'Please input your username!',
          },
        ]}>
          <Input />
        </Form.Item >
        <Form.Item label="Mentor Email" rules={[
          {
            required: true,
            message: 'Please input your username!',
          },
        ]}>
          <Input />
        </Form.Item>
        <Form.Item label="Confirm Mentor Email" rules={[
          {
            required: true,
            message: 'Please input your username!',
          },
        ]}>
          <Input />
        </Form.Item>
        <Form.Item label="Select Companies" rules={[
          {
            required: true,
            message: 'Please input your username!',
          },
        ]}>
         <Select mode="multiple" >
          <Option value="red">Red</Option>
          <Option value="green">Green</Option>
          <Option value="blue">Blue</Option>
        </Select>
        </Form.Item>
        <Form.Item style={{paddingLeft: 162}}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default AddMentor;
