import React, { useState } from "react";
import { Table } from 'antd';
import RescheduleButton from '../Parts/buttonReschedule'
import SelectionButton from '../Parts/buttonSelection'

const TableReschedule = ({pendingMeetings}) => {

const [possibleMeetings, setPossibleMeetings] = useState([]);
const [view, setView] = useState(0);
const columnsAction = [
  {
    title: 'mentor',
    width: 100,
    dataIndex: 'mentor',
    key: 'mentor',
    fixed: 'left',
  },
  {
    title: 'Companies',
    width: 100,
    dataIndex: 'Companies',
    key: 'Companies',
    fixed: 'left',
  },
  {
    title: 'Action',
    key: 'operation',
    fixed: 'right',
    width: 100,
    render: (record) => <RescheduleButton record={record} setPossibleMeetings={setPossibleMeetings} setView={setView}/> ,
  },
];

const columnsSelection = [
  {
    title: 'mentor',
    width: 100,
    dataIndex: 'Mentor',
    key: 'mentor',
    fixed: 'left',
  },
  {
    title: 'Day',
    width: 100,
    dataIndex: 'Day',
    key: 'Day',
    fixed: 'left',
  },
  {
    title: 'Block',
    width: 100,
    dataIndex: 'Block',
    key: 'Block',
    fixed: 'left',
  },
  {
    title: 'Action',
    key: 'operation',
    fixed: 'right',
    width: 100,
    render: (record) => <SelectionButton view={view} record={record} setPossibleMeetings={setPossibleMeetings} setView={setView}/> ,
  },
];
  const pageView =  [<Table 
  columns={columnsAction} 
  dataSource={pendingMeetings} 
  scroll={{ x: "max-content" }}  
  pagination={{ pageSize: 50 }} 
  />,
  <Table 
  columns={columnsSelection} 
  dataSource={possibleMeetings} 
  scroll={{ x: "max-content" }}  
  pagination={{ pageSize: 50 }} 
  />];
  return (
    <>
    {pageView[view]}
    </>
    );
  }

export default TableReschedule;
