import React from "react";
import { Button } from "antd";

const PopButtons = ({ text, record , setPopUp}) => {
  const cancelMeeting = (e) => {
    setPopUp(false);
    console.log(JSON.stringify([{"mentor": record.Mentor, "Companies": [text]}]));
    fetch(
          "http://localhost:3033/api/meetings" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify([{"mentor": record.Mentor, "Companies": [text]}]),
          }
        )
          .then((response) => response.json())
          .then((result) => {
            console.log(result);
          })
          .catch((error) => {
            console.log("Error:", error);
          });

  };

  return (
    <div>
      <Button type="link" onClick={cancelMeeting} value={text}>
        Cancel Meeting
      </Button>
      <br />
      <Button type="link" onClick={cancelMeeting}>
        Posible Reschedulings
      </Button>
    </div>
  );
};

export default PopButtons;
