import React, { useState } from 'react';
import {
  Form,
  Button,
  Select,
  Space,
} from 'antd';


const DeleteMentor = ({mentors}) => {

  const cancelMentor = (value) => {
    let bodyData = JSON.stringify({"value": value});
    console.log(bodyData);
    fetch(
          "http://localhost:3033/api/mentors" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "Delete",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: bodyData,
          }
        )
          .then((response) => response.json())
          .then((result) => {
              alert(result);
          })
          .catch((error) => {
            alert("Error:", error);
          });
  };
  const listMentors = mentors.map((row) => (<Select.Option value={row.mentor}>{row.mentor}</Select.Option>));
  return (
    <>
       <Space><h1>Delete Mentor</h1></Space>
      <Form
        name='Delete Mentor'
        labelCol={{
          span: 7,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="Horizontal"
        initialValues={{
          size: 'small',
        }}
        onFinish={cancelMentor}
      >
        <Form.Item name='mentorName' label="Select Mentor">
          <Select>
            {listMentors}  
          </Select>
        </Form.Item>
        <Form.Item style={{paddingLeft: 143}}>
          <Button type="primary" htmlType="submit">
          Submit
        </Button>
        </Form.Item>
      </Form>
    </>
  );
};


export default DeleteMentor;
