import React, { useState } from "react";
import { Popover } from "antd";
import "../styles/CellPopOver.css";
/**
 * component that renders the popup menu of each cell on tableschedule
 * props:
 * @param {text} : the company name for the cell
 * @param {row}: the row belonging to the company to select the mentor and
 * fetch the cancel action to the backend
 * @returns None
 */
const CellPopUp = ({ text, row }) => {
  /* Handles the popUp action on the cell table */
  const [popUp, setPopUp] = useState(false);

  const cancelMeeting = () => {
    fetch(
      "http://localhost:3033/api/meetings" /* Route to send the CSV file to 
                                                    generate the schedule */,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: jsonData,
      }
    )
      .then((response) => response.json())
      .then((result) => {
        /*result is the generated schedule without conflicts*/
        setResSchedule(result);
        setIsReset(true);
        /* with this we call the use effect to redirect to other component and clean 
              the actual component */
        setFetched(true);
      })
      .catch((error) => {
        setIsReset(true);
        setJsonData(null);
        console.error("Error:", error);
      });
  };

  const handleVisiblePopUp = (visible) => {
    setPopUp(popUp ? false : true);
  };
  return (
    <Popover
      content={
        <div>
          <a onClick={cancelMeeting}>Cancel Meeting</a>
          <br />
          <a onClick={cancelMeeting}>Posible Reschedulings</a>
        </div>
      }
      title="Title"
      trigger="click"
      visible={popUp}
      onVisibleChange={handleVisiblePopUp}
    >
      <a id="cellPop">
        <div>{text}</div>
      </a>
    </Popover>
  );
};

export default CellPopUp;
