import React, { useRef, useState, useEffect} from "react";
import { Button } from "antd";

const SelectionButton = ({ record, changeMeetings }) => {


  const handleFetch = () => {
      changeMeetings({'currentView': 0});
    }


  const cancelMeeting = (target) => {
    console.log(record);
    fetch(
          "http://localhost:3033/api/reschedule" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify(record),
          }
        )
          .then((response) => response.json())
          .then((result) => {
              alert(result.message);
          })
          .catch((error) => {
            console.log("Error:", error);
          });


  };

  return (
    <>
      <Button type="primary" onClick={cancelMeeting} onChange={handleFetch} style={{borderRadius: 5}}>
        Program meetings
      </Button>
    </>
  );
};

export default SelectionButton;
