import React, { useState } from "react";
import { Popover, Button } from "antd";
import "../styles/CellPopOver.css";

const CellPopUp = ({ text }) => {
  /* Handles the popUp action on the cell table */
  const [popUp, setPopUp] = useState(false);

  const hide = () => {
    setPopUp(false);
  };

  const handleVisibleChange = (visible) => {
    setPopUp(popUp ? false : true);
  };
  return (
    <Popover
      content={
        <div>
          <a onClick={hide}>Cancel Meeting</a>
          <br />
          <a onClick={hide}>Posible Reschedulings</a>
        </div>
      }
      title="Title"
      trigger="click"
      visible={popUp}
      onVisibleChange={handleVisibleChange}
    >
      <a id="cellPop">
        <div>{text}</div>
      </a>
    </Popover>
  );
};

export default CellPopUp;
