import React from "react";
import { Button } from "antd";

const SelectionButton = ({ view, record, setPossibleMeetings, setView }) => {


  const cancelMeeting = (target) => {
    fetch(
          "http://localhost:3033/api/meetings" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify([record]),
          }
        )
          .then((response) => response.json())
          .then((result) => {
              alert(result.message);
              setView(0);
          })
          .catch((error) => {
            console.log("Error:", error);
          });


  };
  const buttonMessage = ['Reschedule', 'Schedule'];
  let message = buttonMessage[view];
  return (
    <>
      <Button type="primary" onClick={cancelMeeting}>
        Program meetings
      </Button>
    </>
  );
};

export default SelectionButton;
