import React, { useState } from "react";
import { Popover, Button } from "antd";
import "../styles/CellPopOver.css";
/**
 * component that renders the popup menu of each cell on tableschedule
 * props:
 * @param {text} : the company name for the cell
 * @param {row}: the row belonging to the company to select the mentor and
 * fetch the cancel action to the backend
 * @returns None
 */
const CellPopUp = ({ text }) => {
  /* Handles the popUp action on the cell table */
  const [popUp, setPopUp] = useState(false);

  const cancelMeeting = (target) => {
    let cancel_company = target.value;
    console.log(cancel_company);
  };

  const handleVisiblePopUp = (visible) => {
    setPopUp(popUp ? false : true);
  };
  return (
    <Popover
      content={
        <div>
          <Button type="link" onClick={cancelMeeting} value={text}>
            Cancel Meeting
          </Button>
          <br />
          <a onClick={cancelMeeting}>Posible Reschedulings</a>
        </div>
      }
      title="Title"
      trigger="click"
      visible={popUp}
      onVisibleChange={handleVisiblePopUp}
    >
      <a id="cellPop">
        <div>{text}</div>
      </a>
    </Popover>
  );
};

export default CellPopUp;
