import React, { useState } from 'react';
import '../styles/uploadFile.css';

const FileUploadPage = () => {
    const [selectedFile, setSelectedFile] = useState();
    const [isSelected, setIsSelected] = useState(false);

    const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		setIsSelected(true);
        alert(selectedFile.name);
	};

	const handleSubmission = () => {

        const formData = new FormData();
		formData.append('File', selectedFile);

		fetch(
			'https://freeimage.host/api/1/upload?key=6d207e02198a847aa98d0a2a901485a5',
			{
				method: 'POST',
				body: formData,
			}
		)
			.then((response) => response.json())
			.then((result) => {
				console.log('Success:', result);
			})
			.catch((error) => {
				console.error('Error:', error);
			});
	};

	return (
   <div>
			<input type="file" name="file" onChange={changeHandler} accept="text/csv"/>
			{isSelected ? (
				<div>
					<p className="paragraph">Filename: {selectedFile.name}</p>
					<p className="paragraph">Filetype: {selectedFile.type}</p>
					<p className="paragraph">Size in bytes: {selectedFile.size}</p>
					<p className="paragraph">
						lastModifiedDate:{' '}
						{selectedFile.lastModifiedDate.toLocaleDateString()}
					</p>
				</div>
			) : (
				<p>Select a file to show details</p>
			)}
			<div>
				<button onClick={handleSubmission}>Submit</button>
			</div>
	</div>
		);
};

export default FileUploadPage;
