import React from "react";
import { Button } from "antd";

const RescheduleButton = ({ view, record, setPossibleMeetings, setView }) => {


  const cancelMeeting = (target) => {

      console.log(target);
    fetch(
          "http://localhost:3033/api/reschedule" /* Route to send the CSV file to 
                                                    generate the schedule */,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify([record]),
          }
        )
          .then((response) => response.json())
          .then((result) => {
            setPossibleMeetings(result);
            setView(1);
          })
          .catch((error) => {
            console.log("Error:", error);
          });


  };
  const buttonMessage = ['Reschedule', 'Schedule'];
  let message = buttonMessage[view];
  return (
    <>
      <Button type="primary" onClick={cancelMeeting}>
        Reschedule
      </Button>
    </>
  );
};

export default RescheduleButton;
