import React, { useState } from 'react';
import '../assets/styles/Survey.css'
import { Avatar, Slider, Switch, Popover, Radio, Button } from 'antd';
/* import Lock from './Lock'; */
import { UserOutlined, LockOutlined, UnlockOutlined } from '@ant-design/icons';
import { AiOutlineCheckCircle, AiOutlineBulb, AiOutlineCloseCircle } from "react-icons/ai";

function Survey(props) {

  const [state, setState] = useState(true);
  const [cant, setView] = useState(true);
  const content = (
    <div className='popcover'>
      <p>Cant touch this! ♫♪♪</p>
      <p>Click below to undo</p>
    </div>
  );
  const marks = {
    1: '1',
    2: '2',
    3: '3',
    4: '4',
    5: {style: {color: '#39C463'},label: <strong>5</strong>,},
  };

  function onChange(){
    setState(!state)
    console.log(state)
  };

  function onDisable(){
    setView(!cant)
    console.log(cant)
  };

  return(
        <div className="container">
          <div className="survey-box">
            <Avatar size={80} icon={<UserOutlined />} />
            <span>{props.meetings}</span>
            <div className='buttons'>
              {/* <button className='button1'><AiOutlineCheckCircle size={20}/><span className='icontxt'>Want To</span></button>
              <button className='button2'><AiOutlineBulb size={20}/><span className='icontxt'>Willing</span></button>
              <button className='button3'><AiOutlineCloseCircle size={20}/><span className='icontxt'>Won't</span></button> */}
              <Radio.Group buttonStyle="solid">
                <Radio.Button onClick={() => {onDisable()}} style={{ background: "#39C643",
                                        borderRadius: "15px 0px 0px 15px" }} value="a"><AiOutlineCheckCircle size={15}/>Want To</Radio.Button>
                <Radio.Button onClick={() => {onDisable()}} style={{ background: "#ff9800" }} value="b"><AiOutlineBulb size={15}/>Willing</Radio.Button>
                <Radio.Button onClick={() => {onDisable()}} style={{ background: "#ff0000",
                                        borderRadius: "0px 15px 15px 0px"}} value="c"><AiOutlineCloseCircle size={15}/>Won't</Radio.Button>
              </Radio.Group>
            </div>
            <div className='slider'>
              <p className='slidetxt'>Mentor Helpfullness</p>
              <Slider min={1} max={5} marks={marks} defaultValue={1}/>
            </div>
            <div>
              <p className='slidetxt'>Please Explain: (Optional)</p>
            </div>
            <textarea className='feedback' maxlength = "250" cols="10" rows="5" placeholder="Type here" name="Feedback" />
            <div className='lock'>
            <Popover placement="top" content={content} visible={!state} style={{}}>
            <Button
              disabled="true"
              style={{ margin: '0', background: "#39C643" }}
              type="secondary" ghost
              size="large"
              shape="round"
              onClick={() => {onChange()}}
            >
              {!cant ? <Radio.Group disabled={true}/> : <Radio.Group disabled={false}/> }
              {!state ? "Undo" : "Submit" }
              {!state ? <LockOutlined /> : <UnlockOutlined />}
            </Button>
            </Popover>
            </div>
            {/* <button className='button'>Submit</button> */}
          </div>
        </div>
  )
}
export default Survey;
