const types = [<button className='button1'>Want To</button>, <button className='button2'>Willing</button>, <button className='button3'>Won't</button>];
function ToggleGroup() {
  const [active, setActive] = useState(types[0]);
  return (
      types.map(type => (
        <button
          key={type}
          active={active === type}
          onClick={() => setActive(type)}
        >
          {type}
        </button>
      ))
  );
}
export default ToggleGroup;
