import React, { useState } from 'react';
import '../assets/styles/Survey.css';
import { Avatar, Slider, Radio, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import {
  AiOutlineCheckCircle,
  AiOutlineBulb,
  AiOutlineCloseCircle,
} from 'react-icons/ai';
import ReactCardFlip from 'react-card-flip';

function Survey(props) {
  const [state, setState] = useState(true);
  const [flag, setFlag] = useState(false);
  const [flip, isFlipped] = useState(true);
  const marks = {
    1: '1',
    2: '2',
    3: '3',
    4: '4',
    5: { style: { color: '#39C463' }, label: <strong>5</strong> },
  };

  function onChange() {
    setState(!state);
    isFlipped(!flip);
    console.log(state);
  }

  return (
    <div className='container'>
      {/* Front of the card */}
      <ReactCardFlip isFlipped={!flip} flipDirection='horizontal'>
        <div className='survey-box-front'>
          <Avatar size={80} icon={<UserOutlined />} />
          <span>{props.meetings}</span>
          <div className='buttons'>
            <Radio.Group buttonStyle='solid' defaultValue={props.vals}>
              <Radio.Button
                style={{
                  background: '#39C643',
                  borderRadius: '15px 0px 0px 15px',
                }}
                value={1}
                /* llamar funcion si la data no es null*/
                onClick={() => setFlag(true)}
              >
                <AiOutlineCheckCircle size={15} />
                Want To
              </Radio.Button>
              <Radio.Button
                style={{ background: '#ff9800' }}
                value={2}
                onClick={() => setFlag(true)}
              >
                <AiOutlineBulb size={15} />
                Willing
              </Radio.Button>
              <Radio.Button
                style={{
                  background: '#ff0000',
                  borderRadius: '0px 15px 15px 0px',
                }}
                value={3}
                onClick={() => setFlag(true)}
              >
                <AiOutlineCloseCircle size={15} />
                Won't
              </Radio.Button>
            </Radio.Group>
          </div>
          <div className='slider'>
            <p className='slidetxt1'>{props.txt}</p>
            <Slider min={1} max={5} marks={marks} defaultValue={1} />
          </div>
          <div>
            <p className='slidetxt2'>Please Explain: (Optional)</p>
          </div>
          <textarea
            className='feedback'
            maxlength='250'
            cols='10'
            rows='5'
            placeholder='Type here'
            name='Feedback'
          />
          <div className='lock'>
            <Button
              style={{ margin: '0', background: '#39C643' }}
              type='secondary'
              size='large'
              shape='round'
              disabled={flag ? false : true}
              ghost={flag ? false : true}
              onClick={() => {
                onChange();
              }}
            >
              {!state ? 'Undo' : 'Submit'}
            </Button>
          </div>
        </div>

        {/* Back of the card */}

        <div className='survey-box-back'>
          <div className='back'>
            <AiOutlineCheckCircle size={100} color={'#39C643'} />
            <p>Thank you for your feedback!</p>
            <Button
              style={{ margin: '0', background: '#39C643' }}
              type='secondary'
              size='large'
              shape='round'
              disabled={flag ? false : true}
              ghost={flag ? false : true}
              onClick={() => {
                onChange();
              }}
            >
              {!state ? 'Undo' : 'Submit'}
            </Button>
          </div>
        </div>
      </ReactCardFlip>
    </div>
  );
}
export default Survey;
