import React, { useState } from "react";
import { Button } from 'antd';
import { AiOutlineCheckCircle, AiOutlineBulb, AiOutlineCloseCircle } from "react-icons/ai";
import styled from "styled-components";
import '../assets/styles/Survey.css'

/* const Button = styled.button`
  width: 45%;
      background-color: #39C463;
      padding: 15px;
      box-sizing: none;
      font-family: "Suisse Int'l Black";
      border-width: 2px;
      border-style: none;
      outline: none;
      border-radius: 20px;
      background-image: -webkit-linear-gradient( 90deg,  rgb(25, 85, 29) 5%, rgb(57,198,66) 100%);
`; */
const ButtonToggle = styled(Button)`
  opacity: 0.6;
  marging: 10px;
  ${({ active }) =>
    active &&
    `
    opacity: 1;
  `}
`;
/* const ButtonGroup = styled.div`
  display: flex;
`; */
const types = [<>Want To</>, <>Willing</>, <>Won't</>];
function ToggleGroup() {
  const [active, setActive] = useState(types[0]);
  return (
      types.map(type => (
        <ButtonToggle
          key={type}
          active={active === type}
          onClick={() => setActive(type)}
        >
          {type}
        </ButtonToggle>
      ))
  );
}
export default ToggleGroup;
