import { Button } from 'antd';
import { LockOutlined, UnlockOutlined } from '@ant-design/icons';
import React, { useState } from 'react';
import '../assets/styles/Survey.css'

function Lock(){
  const [state, setState] = useState(true);

  function toggleChecked(){
    setState(true);
  };

  function toggleDisable(){
    setState(false);
  };

  function onChange(event){
    setState(!state)
    if (state) {
    event.target.style.background = '#39C643'
  }
  else
    { event.target.style.background = '#860000'}
  };

  return (
    <>
      <p>
          {/* <Button type="primary" size="small" onClick={() => onChange()}>
            {!state ? 'Check' : 'Uncheck'}
          </Button> */}
          <Button
            style={{ margin: '0' }}
            type="primary"
            size="large"
            shape="round"
            onClick={(event) => {onChange(event)}}
          >
            {!state ? <LockOutlined /> : <UnlockOutlined />}
          </Button>
        </p>
    </>
  );
}
export default Lock;
