import React, { useEffect, useState } from 'react';
import { getData } from './Data';
import Survey from './Survey';
import { Row, Col } from 'antd';
import '../assets/styles/Survey.css';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function Name() {
  const [list, setList] = useState([]);
  const [mentorName, setMentorName] = useState('');
  const id = useLocation().pathname.slice(8);

  useEffect(() => {
    let mounted = true;
    getData().then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    let name = getName(id);
    setMentorName(name);
    return () => (mounted = false);
  }, []);
  async function getName(id) {
    await axios
      .get(`https://techstars-api.herokuapp.com/api/mentors/${id}`)
      .then((name) => {
        console.log(name.data.mentor);
        return name.data.mentor;
      })
      .catch((e) => {
        console.error(e);
      });
  }

  return (
    <Row
      justify='center'
      align='top'
      gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}
    >
      {list.map((meet, i) => {
        if (meet.mentor === mentorName /* && meet.slot */) {
          return (
            <Col key={i} meetings={meet.company}>
              <Survey meetings={meet.company} vals={0}>
                {meet.company}
              </Survey>
            </Col>
          );
        }
      })}
    </Row>
  );
}
export default Name;
