import React from 'react';
import { Menu, Dropdown } from 'antd';
import { Link } from 'react-router-dom';
import { SettingOutlined } from '@ant-design/icons';
import { Data } from "../data";
import logoUrl from '../assets/static/logo-dark.png';
import '../assets/styles/header.css';

function DMenu({setView, viewSelect}) {
  const handleView = ({ key }) => {
    console.log(key);
    setView(key);
    // setViewSelect([key]);
    }

    const menu = (
      <Menu theme='dark' selectedKeys={viewSelect} mode="inline" onClick={handleView}>
            {
              Data.map((mentor)=>
                  {return Object.values(mentor.Slots).map((sub, i)=>
                    {return(sub ?
                      <Menu.Item overlay={DMenu} key={i} company={sub.Slots} icon={<SettingOutlined />}>
                        <Link to={'/'+ mentor.Mentor + '/' + sub}>{sub}</Link>
                      </Menu.Item>
                        : <></>
                    )}
                  )}
              )
            }
        </Menu>
    )
    return (
        <>
        <div className="logo">
          <a  href="https://techstars.com">
          <img src={logoUrl} alt="techstarts icon" />
          </a>
        </div>
        <Dropdown overlay={menu}>
          Companies
        </Dropdown>
        </>
    )
}
export default DMenu;
